<?phpclass ControllerPrilDevicedel extends Controller {	public function index() {		if (isset($this->request->get['imei'])) {			print_r($this->request->get['imei']);						//$imei = preg_replace("/[^a-zA-Z0-9]/", '', $this->request->get['imei']);			$imei = htmlspecialchars($this->request->get['imei'], ENT_QUOTES);						$this->load->model('pril/devicesupdate');						$this->model_pril_devicesupdate->delDevice($imei);		}		}}