<?phpclass ControllerPrilDevicesupdateperformer extends Controller {	public function index() {		if (isset($this->request->get['imei']) || isset($this->request->get['performer'])) {			print_r($this->request->get['imei'].$this->request->get['performer']);			$imei 	= preg_replace("/[^a-zA-Z0-9]/", '', $this->request->get['imei']);			$performer = preg_replace("/[^0-9]/", '', $this->request->get['performer']);						$this->load->model('pril/devicesupdate');						$this->model_pril_devicesupdate->updateDevicePerformer($imei, $performer);		}		}}